import React, { Component, Fragment } from 'react';
import { render } from 'react-dom';
import ScreenCapture from './ScreenCapture'
import './style.css';

class App extends Component {
  state = {
    screenCapture: ''
  }

  handleScreenCapture = (screenCapture) => {
    this.setState({screenCapture})
  }

  loadPicture(screenCapture) {
    document.getElementById("myModal").style.display = 'block';
    document.getElementById("img").src = screenCapture;
  }

  closePicture() {
    document.getElementById("myModal").style.display = 'none';
  }


  render() {
    const { screenCapture } = this.state
    return (
      <ScreenCapture onEndCapture={this.handleScreenCapture}>
        {({ onStartCapture }) => (
          <Fragment>

            <button onClick={onStartCapture}>Capture</button>
            <br/>
            <br/>
            <img src={screenCapture} />
          </Fragment>
        )}
        <div id="myModal" className="modal">
          <span className="close" onClick={closePicture}>&times;</span>
          <img className="modal-content" id="img" alt="open"/>
        </div>
        <div className="modal fade" id="reencaminhar" tabIndex="-1" role="dialog" aria-aria-labelledby="reencaminharLabel" aria-hidden="true">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header"> 
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <div className="vertical-center">
                  <div className="inner-block">
                    <Col md={{ span: 8, offset: 2 }}>
                      <Image id="imagem" src={screenCapture} fluid onClick={loadPicture}/>
                    </Col>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </ScreenCapture>
    );
  }
}

render(<App />, document.getElementById('root'));
